sql.where:

  id like concat(concat('%',#{query.id}),'%')


  and name like concat(concat('%',#{query.name}),'%')


  and tel=#{query.tel}


  and type=#{query.type}


  and district=#{query.district}


  and address like concat(concat('%',#{query.address}),'%')


  and (per between #{query.perBegin} and #{query.perEnd})

and del_flg='0'
